import { Socket } from 'engine.io';
import React from 'react';
import socketio from 'socket.io-client'
import "./Chat.css"

class Chat extends React.Component {
    socket = socketio.connect('http://localhost:5000')
    constructor (props) {
        super(props)
        this.state = { username: this.props.location.state.username, message: ''}
    }
    enterMessage = (e) => {
        this.setState({message: e.target.value})
    }
    sendMessage () {
        socket.emit('chat', {
            username: this.state.username,
            message: this.state.message
        });
        //입력 칸 초기화
        this.setState({message: ''});
    }

    render() {
        return (
            <div className="chat-container">
                <div className="chat-header">
                    <button id="btn-back" > {this.state.username} 이름!! </button>
                    Chat 
                </div>
                <div className="chat-view">
                    {/* <div className="my-chat"> 안녕</div>
                    <div className="your-chat"> 구래 안녕 내일 보장</div>
                    <div className="your-chat"> 웅 빠이</div>
                    <div className="my-chat"> 빠이</div> */}
                </div>
                <div className="enter-form">
                    <input type="text" id="message" onChange={this.enterMessage}/>
                    <input type="submit" value="Send" id="send" onSubmit={this.sendMessage}/>
                </div>
            </div>
        );
    }
}
