// import { Socket } from 'engine.io';
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import socketio from 'socket.io-client'
import "./Chat.css"

const socket = socketio.connect('http://localhost:5000',{transports:['websocket']});

function Chat () {
    const [message, setMessage] = useState('');
    const location = useLocation();
    const username = location.state.username;
    const chatview = document.getElementsByClassName('chat-view');
    const enterMessage = (e) => {
        setMessage(e.target.value);
    };
    const sendMessage = (e) => {
        e.preventDefault();
        if (message) {
            socket.emit('sendMessage', { 
                message 
            }, () => setMessage(')'));
        }
    };
    socket.on ('connect', () => {
        socket.emit('newUser', username);
        console.log(username + "연결")
    });
    socket.on ('updateMessage', (data) => {
        const messageWrap = drawChat(data);
        chatview.appendChild(messageWrap);
    })
    const drawChat = (data) => {
        const messageWrap = document.createElement('div');
        const username = document.createElement('div');
        const message = document.createElement('div');

        messageWrap.classList.add('message-wrap');
        username.innerText = data.username;
        username.classList.add('message-usernamae');
        message.innerText = data.message;
        message.classList.add('message');

        messageWrap.appendChild(username);
        messageWrap.appendChild(message);
        return messageWrap;
    }

    return (
        <div className="chat-container">
            <div className="chat-header">
                <button id="btn-back"> {username} </button>
                Chat 
            </div>
            <div className="chat-view">

            </div>
            <form className="enter-form" onSubmit={sendMessage}>
                <input type="text" id="message" onChange={enterMessage}/>
                <input type="submit" value="Send" id="send" />
            </form>
        </div>
    );
}

export default Chat;
