import React from 'react';
import axios from 'axios';
import "./Auth.css"

class Auth extends React.Component {
    state = {
        username: ''
    };
    enterUsername = (e) => {
        this.setState({
            username: e.target.value
        });
        console.log(e.target.value)
        console.log(this.state.username)
    };
    enterChat = (e) => {
        // e.preventDefault();
        const username= this.state.username;
        axios.post ("http://localhost:5000/text", username)
            .then(res => console.log(res));
    };
    render() {
        return (
            <div className="auth-container">
                <form className="login-wrap" onSubmit={this.enterChat}>
                    <input type="text" id="username" placeholder="이름을 입력하세요" onChange={this.enterUsername}></input>
                    <input type="submit" id="btn-submit" value="enter"></input>
                </form>
            </div>
        );
    }
}

