// import { Socket } from 'engine.io';
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import socketio from 'socket.io-client'
import "./Chat.css"

const socket = socketio.connect('http://localhost:5000',{transports:['websocket']});
socket.on('connect',()=>{
    console.log('socket 연결 !!')
})
socket.on('msg', (res) => {
    console.log(res)
})

function Chat () {
    const [message, setMessage] = useState('');
    const location = useLocation();
    const username = location.state.username;
    const enterMessage = (e) => {
        setMessage(e.target.value);
    }
    const sendMessage = (e) => {
        e.preventDefault();
        if(message) {
            socket.emit('message', {
                username: username
            })
            // socket.emit('sendMessage', {
            //     username: this.state.username,
            //     message: this.state.message
            // }, () => setMessage(''));
        }
    }

    return (
        <div className="chat-container">
            <div className="chat-header">
                <button id="btn-back"> {username} </button>
                Chat 
            </div>
            <div className="chat-view">
                {/* <div className="message-wrap my-message"> 
                    <div className="message-usernamae">seulgi</div>
                    <div className="message">안녕</div>
                </div>
                <div className="message-wrap your-message"> 
                    <div className="message-usernamae">seulgi</div>
                    <div className="message">구래 안녕 내일 보장</div>
                </div>
                <div className="message-wrap your-message"> 
                    <div className="message-usernamae">seulgi</div>
                    <div className="message">웅 빠이</div>
                </div>
                <div className="message-wrap my-message"> 
                    <div className="message-usernamae">seulgi</div>
                    <div className="message">빠이</div>
                </div> */}
            </div>
            <form className="enter-form" onSubmit={sendMessage}>
                <input type="text" id="message" onChange={enterMessage}/>
                <input type="submit" value="Send" id="send" />
            </form>
        </div>
    );
}

export default Chat;
